function debug(text)
{
  console.log(text);
}

function chart(text)
{
  // $('#sales-forecast').show().html('chart');
  Shiny.onInputChange('chart', text);
}

function forecast(text)
{
  // alert('forecast');
  // $('#sales-forecast').show().html('forecast');
  Shiny.onInputChange('forecast', text);
}

function downloadReport()
{
    // download
    var link = document.createElement("a");
    link.download = 'report';
    link.href = 'img/report.png';
    link.click();
}

function changeColor(color)
{
    // 填充颜色转换 代码
    //$('#sales-forecast').show().html(text);
    Shiny.onInputChange('color', color);
}

var initVoice = function(commands, annyang, isDebug, Shiny)
{



    if (annyang) {
       console.log('running Voice');
      // Add our commands to annyang
      Shiny.onInputChange('left', '10');
      Shiny.onInputChange('title', 'say title something');
      Shiny.onInputChange('chart', 'scatter');
      Shiny.onInputChange('color', 'black');
      Shiny.onInputChange('bigger', 1);
      Shiny.onInputChange('yes', 'no');
      Shiny.onInputChange('forecast', 'no');
      Shiny.onInputChange('download', 'no');

      annyang.addCommands(commands);
      annyang.debug(isDebug);

      annyang.addCallback('start', function() {
        console.log('starting.....');
      });

      annyang.addCallback('error', function() {
        console.log('There was an error');
      });

      annyang.addCallback('errorNetwork', function(){
          console.log('There was an errorNetwork');
      }, this);

      annyang.addCallback('resultMatch', function(userSaid, commandText, phrases) {
        console.log(userSaid); // sample output: 'hello'
        console.log(commandText); // sample output: 'hello (there)'
        console.log(phrases); // sample output: ['hello', 'halo', 'yellow', 'polo', 'hello kitty']
      });
    }
}

function voiceButton(annyang)
{
    if (annyang) {
        // Tell KITT to use annyang
        SpeechKITT.annyang();

        // Define a stylesheet for KITT to use
        SpeechKITT.setStylesheet('css/flat.css');

        // Render KITT's interface
        SpeechKITT.vroom();
        SpeechKITT.startRecognition();
    }
}


function runVoice(isDebug, Shiny)
{
  // loadAnnyang();
  voiceButton(annyang);
  var commands = {
    'chart': function(text) {
       if (isDebug) debug('chart model');
       chart(text);
    },
    'forecast': function(text) {
       if (isDebug) debug('forecast model');
       forecast(text);
    },
    'download': function(text) {
        if (isDebug) debug('download model');
        downloadReport(text);
    },
    'color *color': function(text) {
        if (isDebug) debug('color model');
        changeColor(text);
    }
  };
  setTimeout(initVoice(commands, annyang, isDebug, Shiny), 5);
}
